'use strict';

var path = require('path');

var _require = require('mrm-core'),
    json = _require.json,
    template = _require.template;

module.exports = function () {
  var pkg = json('package.json');

  // Create Readme.md (no update)
  var readme = template('README.md', path.join(__dirname, '../../templates/README.template'));
  if (!readme.exists()) {
    readme.apply({
      package: pkg.get('name'),
      description: pkg.get('description') || 'DESCRIPTION'
    }).save();
  }
};